export default class OrderSummary {
    constructor(params) {
    //   super();
    //   this.config = config;
      this.data = params.data;
    }
  
    static get toolbox() {
      return {
        title: 'Order Summary',
        icon: `<svg fill="none" stroke="currentColor" stroke-width="1.5" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
        <path stroke-linecap="round" stroke-linejoin="round" d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25H12"></path>
      </svg>`
      };
    }
  
    render() {
      const wrapper = document.createElement('div');
      wrapper.classList.add('orderSummary-wrapper');
  
        wrapper.innerHTML = `
        <img data-link="address" class="settingsIcon" src="data:image/svg+xml;base64,">
        <div class="settingsWrapper" data-area="address">
                            
          <div class="input1">
            <label>Package Wrapper Class</label>
            <input type="text" name="wrapperClass" value="${this.data.wrapperClass ? this.data.wrapperClass : '' }">
          </div>

          <div class="input1">
            <label>Package Title Class</label>
            <input type="text" name="packageClass" value="${this.data.packageClass ? this.data.packageClass : '' }">
          </div> 
          
          <div class="input1">
            <label>Package Price Class</label>
            <input type="text" name="packagePriceClass" value="${this.data.packagePriceClass ? this.data.packagePriceClass : '' }">
          </div>

          <div class="input1">
            <label>Summary Wrapper Class</label>
            <input type="text" name="summaryClass" value="${this.data.summaryClass ? this.data.summaryClass : '' }">
        </div>
      
          
          <div class="input1">
            <label>Summary Title Class</label>
            <input type="text" name="summaryTitleClass" value="${this.data.summaryTitleClass ? this.data.summaryTitleClass : '' }">
          </div> 
          
         
          
          <div class="input1">
            <label>Summary Price Class</label>
            <input type="text" name="summaryPriceClass" value="${this.data.summaryPriceClass ? this.data.summaryPriceClass : '' }">
          </div>

          

          <div class="input1" style="width: 100%;">
            <button data-link="address"> Save </button>
          </div>
          
                                         
        </div>
        
        
          <div class="orderSummary">
            <div>Package Name</div>
            <div>$PRICE</div>
          </div>
          <div class="orderSummarySum">
            <div>SUMMARY</div>
            <div>$PRICE</div>
          </div>
          `;

          let b = wrapper.querySelectorAll('[data-link]')
        for(let i =0; i < b.length; i++){
            b[i].addEventListener('click', () => {
                // this._toggleTune('check1');
                if (b[i] !== undefined){
                    wrapper.querySelector('[data-area='+b[i].dataset.link+']').classList.toggle('active');
                    this.save();
                }
            });
        }

        this.wrapper = wrapper;
      return wrapper;
    }
  
    save() {
        this.data = {

            wrapperClass: this.wrapper.querySelector('[name="wrapperClass"]').value,
            packageClass: this.wrapper.querySelector('[name="packageClass"]').value,
            packagePriceClass: this.wrapper.querySelector('[name="packagePriceClass"]').value,
            summaryClass: this.wrapper.querySelector('[name="summaryClass"]').value,
            summaryTitleClass: this.wrapper.querySelector('[name="summaryTitleClass"]').value,
            summaryPriceClass: this.wrapper.querySelector('[name="summaryPriceClass"]').value,
           // showEdit: this.wrapper.querySelector("[name='showEdit']").checked ? true : false,
        };
      return this.data;
    }
}
   