import { ref } from 'vue';
const fileHandler = ref(null);
	const currentPreview = ref(null);

function setCurrentPreview(url) {
	currentPreview.value = url || null;
}
function unsetFileHandler() {
	fileHandler.value = null;
	currentPreview.value = null;
}
function setFileHandler(handler) {
	fileHandler.value = handler;
}
function handleFile(event) {
	if (fileHandler.value) {
		fileHandler.value(event);
	}
	unsetFileHandler();
}


export default {
	

		fileHandler,
		unsetFileHandler,
		setFileHandler,
		handleFile,
		currentPreview,
		setCurrentPreview,

}

